def square(num1):
  return num1*num1

def add(num1, num2):
  return num1+num2

def subtract(num1, num2):
  return num1-num2

def multiply(num1, num2):
  return num1*num2

def divide(num1, num2):
  return num1/num2

def squareroot(num1):
  return num1/num1

def randomnumber(num1, num2):
  return range(num1, num2)

def stringlettercount(String):
  stringletters = len(String)
  return stringletters

def fahrenheit_to_celsius(num1):
  answer = num1 - 32 * 0.5556
  return answer

def celsius_to_fahrenheit(num1):
  answer = num1 * 1.8 + 32
  return answer