def add_num(x,y):
    print("adding numbers")
    return(x+y)

def sub_num(x,y):
    print("subtracting numbers")
    return(x-y)

def mult_num(x,y):
    print("multiplying numbers")
    return(x*y)

def div_num(x,y):
    return(x/y)

def exp_num(x,y):
    return(x**y)

def try_num(x,y):
    return(x**y+y**x)

