import time

def hi(name):
    print("hi" + name)
    
def add(num1, num2):
    print(num1 + num2)
    
def subtract(num1, num2):
    print(num1 - num2)
    
def divide(num1, num2):
    print(num1 / num2)
    
def times(num1, num2):
    print(num1 * num2)

def wait(timer):
    time.sleep(timer)


